      }
    },
    
    // 시나리오 생성
    async createScenario({ commit, getters }, scenarioData) {
      commit('SET_LOADING', true)
      commit('CLEAR_ERROR')
      
      try {
        // OpenAI API를 통해 시나리오 생성
        const response = await axios.post('/api/scenarios/generate/', scenarioData)
        
        // Firebase Firestore에 시나리오 저장
        const db = getFirestore()
        const scenariosRef = collection(db, 'scenarios')
        
        const newScenario = {
          title: scenarioData.title,
          main_disease: scenarioData.main_disease,
          personal_info: scenarioData.personal_info,
          additional_info: scenarioData.additional_info,
          content: response.data.content,
          created_at: new Date().toISOString(),
          updated_at: new Date().toISOString(),
          created_by: {
            id: getters.currentUser.id,
            username: getters.currentUser.username,
            email: getters.currentUser.email,
            is_admin: getters.currentUser.is_admin
          }
        }
        
        const docRef = await addDoc(scenariosRef, newScenario)
        const scenario = {
          id: docRef.id,
          ...newScenario
        }
        
        commit('SET_LOADING', false)
        return scenario
      } catch (error) {
        commit('SET_ERROR', error.message)
        commit('SET_LOADING', false)
        throw error
      }
    },
    
    // 시나리오 수정
    async modifyScenario({ commit, getters }, { scenarioId, userInput }) {
      commit('SET_LOADING', true)
      commit('CLEAR_ERROR')
      
      try {
        // OpenAI API를 통해 시나리오 수정
        const response = await axios.post('/api/modifications/modify/', {
          scenario_id: scenarioId,
          user_input: userInput
        })
        
        // Firebase Firestore에 수정 기록 저장
        const db = getFirestore()
        const modificationsRef = collection(db, 'modifications')
        
        const modification = {
          scenario_id: scenarioId,
          user_input: userInput,
          modified_content: response.data.modified_content,
          created_at: new Date().toISOString(),
          created_by: {
            id: getters.currentUser.id,
            username: getters.currentUser.username,
            email: getters.currentUser.email,
            is_admin: getters.currentUser.is_admin
          }
        }
        
        const docRef = await addDoc(modificationsRef, modification)
        
        // 시나리오 내용 업데이트
        const scenarioRef = doc(db, 'scenarios', scenarioId)
        await updateDoc(scenarioRef, {
          content: response.data.modified_content,
          updated_at: new Date().toISOString()
        })
        
        // 현재 시나리오 업데이트
        if (getters.currentScenario && getters.currentScenario.id === scenarioId) {
          const updatedScenario = {
            ...getters.currentScenario,
            content: response.data.modified_content,
            updated_at: new Date().toISOString()
          }
          commit('SET_CURRENT_SCENARIO', updatedScenario)
        }
        
        commit('SET_LOADING', false)
        return {
          id: docRef.id,
          ...modification
        }
      } catch (error) {
        commit('SET_ERROR', error.message)
        commit('SET_LOADING', false)
        throw error
      }
    }
  }
})
